package com.psp.ArithmeticDemo;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 5;
		System.out.print( "Addition of two numbers (10,5) = " );
		System.out.println( a  +  b );
		System.out.print( "Subtraction of two numbers (10,5)= " );
		System.out.println( a  -  b );	
		System.out.print( "Multiplication of two numbers (10,5)= " );
		System.out.println( a  *  b );	
		System.out.print( "Division of two numbers (10,5)= " );
		System.out.println( a  /  b );
		System.out.print( "Modulus of two numbers (10,5)= " );
		System.out.println( a  %  b );

	}

}
