package com.psp.operators;

public class ConditionalOperator {

public static void main(String[] args) {

		// TODO Auto-generated method stub
				int a = 20;
				int b;
				
				b = ( a == 1) ? 100 : 200 ;
				System.out.println( " Value of b = " + b);
				
				b = ( a == 20) ? 100 : 200 ;
				System.out.println( " Value of b = " + b);
				
				
				

	}

}

